import React from 'react';

const Counter = ({ boysCount, girlsCount }) => {
  const META = 150;
  const boysProgress = (boysCount / META) * 100;
  const girlsProgress = (girlsCount / META) * 100;

  return (
    <div className="flex justify-center items-center p-8 bg-gray-100 space-x-8">
      <div className="counter-card bg-white p-6 rounded-lg shadow-lg hover:shadow-xl transition-all duration-300 w-80 border-t-4 border-blue-500 hover:transform hover:-translate-y-1">
        <h2 className="text-2xl font-bold text-blue-600 mb-2">ðŸ‘¦ Regalos para NiÃ±os</h2>
        <p className="counter-number text-4xl font-bold text-blue-800">{boysCount}</p>
        <p className="text-lg text-blue-600">Meta: {META}</p>
        <div className="w-full bg-gray-200 rounded-full h-2.5 mt-2">
          <div className="bg-blue-500 h-2.5 rounded-full transition-all duration-500"
               style={{ width: `${boysProgress}%` }}></div>
        </div>
        <p className="text-sm text-gray-600 mt-1">{Math.round(boysProgress)}% completado</p>
      </div>

      <div className="counter-card bg-white p-6 rounded-lg shadow-lg hover:shadow-xl transition-all duration-300 w-80 border-t-4 border-fuchsia-500 hover:transform hover:-translate-y-1">
        <h2 className="text-2xl font-bold text-fuchsia-600 mb-2">ðŸ‘§ Regalos para NiÃ±as</h2>
        <p className="counter-number text-4xl font-bold text-fuchsia-800">{girlsCount}</p>
        <p className="text-lg text-fuchsia-600">Meta: {META}</p>
        <div className="w-full bg-gray-200 rounded-full h-2.5 mt-2">
          <div className="bg-fuchsia-500 h-2.5 rounded-full transition-all duration-500"
               style={{ width: `${girlsProgress}%` }}></div>
        </div>
        <p className="text-sm text-gray-600 mt-1">{Math.round(girlsProgress)}% completado</p>
      </div>
    </div>
  );
};

export default Counter;
