// GiftList.jsx
import React, { useEffect, useState } from 'react';
import { pb } from '../services/pocketbase';

const GiftList = () => {
    const [gifts, setGifts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchGifts = async () => {
            try {
                setLoading(true);
                const records = await pb.collection('gifts').getFullList();
                setGifts(records);
            } catch (err) {
                setError('Error al cargar los regalos');
                console.error('Error:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchGifts();
    }, []);  // No necesitamos incluir pb como dependencia ya que es una instancia estÃ¡tica

    if (loading) return <div>Cargando...</div>;
    if (error) return <div className="error">{error}</div>;

    return (
        <div className="gift-list">
            <h2>Lista de Regalos</h2>
            {gifts.length === 0 ? (
                <p>No hay regalos registrados</p>
            ) : (
                <ul className="gift-items">
                    {gifts.map((gift) => (
                        <li key={gift.id} className="gift-item">
                            <span className="gift-gender">
                                {gift.Generoo === 'Masculino' ? 'ðŸ‘¦' : 'ðŸ‘§'} {gift.Generoo}
                            </span>
                            <span className="gift-description">{gift.Descripcion_JUguete}</span>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default GiftList;
